$(".like").on("click",function(a){var b=this;a.preventDefault();a=$(this).attr("data-id");axios.post("/api/likes/"+a+"/article/",{article:a},{headers:{"Content-Type":"application/x-www-form-urlencoded","X-CSRFToken":csrftoken}}).then((a)=>{a.data.status?$(b).children(".mdi").addClass("text-danger"):$(b).children(".mdi").removeClass("text-danger");$(b).children("span").html(a.data.count)})});
$(".like-comment").on("click",function(a){var b=this;a=$(this).attr("data-id");axios.post("/api/likes/"+a+"/comment/",{comment:a},{headers:{"Content-Type":"application/x-www-form-urlencoded","X-CSRFToken":csrftoken}}).then((a)=>{a.data.status?$(b).removeClass("text-muted").addClass("text-danger"):$(b).removeClass("text-danger").addClass("text-muted");$(b).children("span").html(a.data.count)})});
$('[data-action="bookmark"]').on("click",function(a){var b=this;a.preventDefault();a=$(this).attr("data-id");axios.post("/api/bookmark/"+a+"/article/",{article:a},{headers:{"Content-Type":"application/x-www-form-urlencoded","X-CSRFToken":csrftoken}}).then((a)=>{a.data.status?$(b).children("i").addClass("text-danger"):$(b).children("i").removeClass("text-danger");$(b).children("span").html(a.data.count)})});
$("#top-search").on("keyup",function(a){a=$(this).val();""!=a&&3<a.length&&axios.get("/api/search/?search="+a).then((a)=>{a=a.data;document.getElementById("search-length").innerHTML=a.length;document.getElementById("noti-search").innerHTML="";for(i=0;a.length>i;i++)document.getElementById("noti-search").innerHTML+='<a href="'+a[i].slug+'" class="dropdown-item notify-item"><div class="d-flex"> <img class="d-flex me-2" src="'+a[i].image+'" alt="Generic placeholder image" height="32" width="65"> <div class="w-100"> <h5 class="m-0 font-14">'+
a[i].title+"</h5> </div> </div> </a>"})});
