function like(){var a=$(this),d=a.data("type"),c=a.data("id"),e=a.data("action"),f=a.next();$.ajax({url:"/api/"+d+"/"+c+"/"+e+"/",type:"POST",data:{obj:c,csrfmiddlewaretoken:csrftoken},success(b){a.find("[data-count='like']").text(b.like_count);f.find("[data-count='dislike']").text(b.dislike_count);$("#likedislike").text(b.sum_rating)}});return!1}
function dislike(){var a=$(this),d=a.data("type"),c=a.data("id"),e=a.data("action"),f=a.prev();$.ajax({url:"/api/"+d+"/"+c+"/"+e+"/",type:"POST",data:{obj:c,csrfmiddlewaretoken:csrftoken},success(b){a.find("[data-count='dislike']").text(b.dislike_count);f.find("[data-count='like']").text(b.like_count);$("#likedislike").text(b.sum_rating)}});return!1}$('[data-action="like"]').click(like);$('[data-action="dislike"]').click(dislike);
